import React, { useState } from "react";

// the idea is 
// get props
// insert to table sale + sale_detail 
// then go to page /cart

export default function AddCart(props) {
    console.log(props.prod_id)
    const prod_id = props.prod_id
    const color = props.color
    const size = props.size
    const sale_amount = props.sale_amount
    const prod_price = props.prod_price
    const [cust_id,setCustId] = useState([])
    // const [prod_id,color, size, sale_amount,prod_price] = props;

    const insertCart = async (e)=>{
        e.preventDefault();
        try {
            const bodyData = {prod_id,color,size,sale_amount,prod_price}
            const res = await fetch(`http://localhost:4000/insertToCart`,{
              method:"POST",
              headers:{"Content-Type": "application/json"},
              body: JSON.stringify(bodyData)
            })
            window.location = "http://localhost:3000/viewproduct"
            console.log(res)
        } catch (err) {
            console.error(err.message)
        }
      }

  return (
    <div>

<div>
        {/* Button to Open the Modal */}
        <button
          type="button"
          className="btn btn-primary"
          data-toggle="modal"
          data-target= {`#id${id}`} //"#myModal"
        >
          Add To Cart
        </button>
        {/* The Modal */}
        <div className="modal " id={`id${id}`}>
          <div className="modal-dialog">
            <div className="modal-content bg-primary">
              {/* Modal Header */}
              <div className="modal-header">
                <h4 className="modal-title">Please Enter Customer ID</h4>
                <button type="button" className="close" data-dismiss="modal">
                  ×
                </button>
              </div>
              {/* Modal body */}
              <div className="modal-body">
                <div>
                  <div className="form-group">
                    <input
                      type="text"
                      className="form-control"
                      id="usr"
                      value={"C9999"}
                      onChange={e=>{setCustId(e.target.value)}}
                    />
                  </div>
                  <div className="form-group">
                    <input
                      type="number"
                      className="form-control"
                      id="pwd"
                      value={price}
                      onChange={e=>{setPrice(e.target.value)}}
                    />
                  </div>
                </div>
              </div>
              {/* Modal footer */}
              <div className="modal-footer">
              <button
                  type="button"
                  className="btn btn-success"
                  data-dismiss="modal"
                  onClick={e=>{insertCart(e)}}
                >
                  Save
                </button>
                <button
                  type="button"
                  className="btn btn-danger"
                  data-dismiss="modal"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
        <h5>Detail:[{prod_id},{color}, {size}, {sale_amount},{prod_price}]</h5>
      <button type="submit" className="btn btn-success text-center" onClick={e=>{insertCart(e)}}>
        Add to cart
      </button>
      
    </div>
  );
}
