import React, { useEffect, useState } from "react";
import Form from "react-bootstrap/Form";

export default function SelectSize() {
  const [size_list, setList] = useState([]);

  const loadList = async () => {
    try {
      const resp = await fetch(`http://localhost:5000/getSizeChart`);
      const jsonData = await resp.json();

      setList(jsonData);

      console.log("Response", resp);
      console.log("List: ", size_list);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    console.log("Enter useEffect");
    loadList();
  }, []);

  return (
    <>
      <Form.Select aria-label="Default select">
        
        <option value="S">One</option>
        <option value="M">Two</option>
        <option value="L">Three</option>
        <option value="XL">One</option>
        <option value="2">Two</option>
        <option value="3">Three</option>
      </Form.Select>
    </>
  );
}
