import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import Form from "react-bootstrap/Form";
import EditBuyDetail from "./EditBuyDetail";

export default function Chart() {

  const [list,setList] = useState();
  const [buy_id,setID] = useState([]);
  const [buy_list,setBuylist] =useState([]);

  const deleteItem = async (full_prod_id) => {
    try {
      const del = await fetch(
        `http://localhost:5000/deleteBuydetail/${full_prod_id}`,
        {
          method: "DELETE",
        }
      );
      setList(
        list.filter((elt) => {
          return elt.full_prod_id !== full_prod_id;
        })
      );
    } catch (err) {
      console.error(err.message);
    }
  };
  const loadList = async () => {
    try {
      const resp = await fetch(`http://localhost:5000/getBuydetail/${buy_id}`);
      const jsonData = await resp.json();

      setList(jsonData);

      console.log("Resp", resp);
      console.log("List:", list);
    } catch (err) {
      console.error(err.message);
    }
  };
  useEffect(() => {
    console.log("Enter useEffect()");
    loadList();
  }, [buy_id]);
  return (
    <div className="container">
      <h2>Chart</h2>
      <tr className="btn mt-3">
        <td>
          <Form.Control
          class="form-control mr-sm-2"
          type="search"
          placeholder="Search"
          onChange={(e) =>{
            setID(e.target.value)
          }}
          />
          {buy_list.map((item) => {
            return <p>{item.buy_id}</p>
          })}
        </td>
        <td>
          <a href="/insertbuydetail">
            <button className="btn btn-success ml-3 ">เพิ่มข้อมูล</button>
          </a>
        </td>
      </tr>
      <table className="table table-striped ">
        <thead>
          <tr>
            <th>Buy Id</th>
            <th>Item</th>
            <th>Full Product Id</th>
            <th>Product Name</th>
            <th>Color</th>
            <th>Size</th>
            <th>Buy Amount</th>
            <th>Buy Cost</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {list.map((elt) => {
            return (
              <tr>
                <td>{elt.buy_id}</td>
                <td>
                  <a>{elt.item}</a>
                </td>
                <a href="/warehouse"><td>{elt.full_prod_id}</td></a>
                <td>{elt.prod_name}</td>
                <td>{elt.color}</td>
                <td>{elt.size}</td>
                <td>{elt.buy_amount}</td>
                <td>{elt.buy_cost}</td>
                <td>
                  <EditBuyDetail
                    buy_id={elt.buy_id}
                    item={elt.item}
                    full_prod_id={elt.full_prod_id}
                    prod_name={elt.prod_name}
                    color={elt.color}
                    size={elt.size}
                    buy_amount={elt.buy_amount}
                    buy_cost={elt.buy_cost}
                  />
                </td>
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => deleteItem(elt.full_prod_id)}
                  >
                    Del
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
      <Bar
        data={{
          labels: ["Red", "Blue", "Yellow", "Green", "Purple", "Orange"],
          datasets: [
            {
              label: "chart1",
              data: [12, 19, 3, 5, 2, 3],
              backgroundColor: [
                "rgba(255, 99, 132, 0.5)",
                "rgba(54, 162, 235, 0.5)",
                "rgba(255, 206, 86, 0.5)",
                "rgba(75, 192, 192, 0.5)",
                "rgba(153, 102, 255, 0.5)",
                "rgba(255, 159, 64, 0.5)",
              ],
              borderColor: [
                "rgba(255, 99, 132, 1)",
                "rgba(54, 162, 235, 1)",
                "rgba(255, 206, 86, 1)",
                "rgba(75, 192, 192, 1)",
                "rgba(153, 102, 255,1)",
                "rgba(255, 159, 64, 1)",
              ],
              borderWidth: 1,
            },
            {
                label: "chart2",
              data: [15, 15, 15, 15, 15, 15],
              backgroundColor:"blue"
            }
          ],
        }}
        width={100}
        height={50}
        options={{ maintainAspectRatio: true }}
      />
    </div>
  );
}
