import React, { useEffect, useState } from "react";
import AddQty from "./AddQty";
import Banner from "./Banner";
import SelectSize from "./SelectSize";

export default function ViewProduct() {
  const id = "P001-BK";
  const [prod_list, setList] = useState([]);

  const loadList = async () => {
    try {
      const resp = await fetch(`http://localhost:5000/getProduct/${id}`);
      const jsonData = await resp.json();

      setList(jsonData);

      console.log("Response", resp);
      console.log("List: ", prod_list);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    console.log("Enter useEffect");
    loadList();
  }, []);

  return (
    <div>
      {/* Featured Product start */}
      <section className="single-product">
        <div className="container">
          {prod_list.map((prod) => {
            const prodLink = "/viewproduct/" + prod.prod_color_id;
            console.log(prodLink);
            return (
              <>
                <div className="row">
                  {/* image of shirt section */}
                  <div className="col-sm-4">
                    <div className="product-item">
                      <figure className="product-thumb">
                        <img src={prod.image_url} alt />
                        <div className="action-links">
                          <a href="/viewproduct" className="quick-view icon">
                            <i className="ti-eye" />
                          </a>
                          <a href="#" className="wishlist icon">
                            <i className="ti-write" />
                          </a>
                          {/* <a href="#" className="wishlist icon">
                                        <i className="ti-heart"/>
                                        </a> */}
                          <a href="#" className="add-cart icon">
                            <i className="ti-shopping-cart" />
                          </a>
                        </div>
                      </figure>
                      <div className="product-content">
                        {/* <h5 className="product-name">
                          <a href="#">{prod.prod_name}</a>
                        </h5>
                        <h5 className="product-name">
                          <a href="#">{prod.color}</a>
                        </h5> */}
                        <div className="ratings">
                          <a href="#">
                            <i className="ti-star" />
                          </a>
                          <a href="#">
                            <i className="ti-star" />
                          </a>
                          <a href="#">
                            <i className="ti-star" />
                          </a>
                          <a href="#">
                            <i className="ti-star" />
                          </a>
                          <a href="#">
                            <i className="ti-star" />
                          </a>
                        </div>
                        {/* <p className="price text-danger">฿{prod.prod_price}</p> */}
                      </div>
                    </div>
                  </div>

                  {/* Detail section */}
                  <div className="col text-left ml-5">
                    <div className="">
                      <h1 className="">{prod.prod_name}</h1>
                    </div>
                    <div className="">
                      <p className="prod_id">Product ID: {prod.prod_id}</p>
                      <p className="color">Color: {prod.color}</p>
                      <p className="size ml-2">Size:
                      <SelectSize/>
                      </p>
                      <p className="quantity">- 1 +</p>
                      <h3 className="price text-danger">฿{prod.prod_price}</h3>
                    </div>
                    <button href="#" className="btn btn-success text-center">
                      Add to cart
                    </button>
                  </div>
                </div>
              </>
            );
          })}
        </div>
      </section>
      {/* Featured Product end */}
    </div>
  );
}
