import React, { useState, useEffect } from "react";
import EditBuyDetail from "./EditBuyDetail";
import Form from "react-bootstrap/Form";

export default function Buydetail() {
  // const id = props.id;
  const [list, setList] = useState([]);
  const [buy_id,setID] = useState([]);
  const [buy_list,setBuylist] = useState([]);

  const deleteItem = async (full_prod_id) => {
    try {
      const del = await fetch(
        `http://localhost:5000/deleteBuydetail/${full_prod_id}`,
        {
          method: "DELETE",
        }
      );
      setList(
        list.filter((elt) => {
          return elt.full_prod_id !== full_prod_id;
        })
      );
    } catch (err) {
      console.error(err.message);
    }
  };
  const loadList = async () => {
    try {
      const resp = await fetch(`http://localhost:5000/getBuydetail/${buy_id}`);
      const jsonData = await resp.json();

      setList(jsonData);

      console.log("Resp", resp);
      console.log("List:", list);
    } catch (err) {
      console.error(err.message);
    }
  };
  const loadID = async () => {
    try {
      const resp = await fetch(`http://localhost:5000/getBuydetail/${buy_id}`);
      const jsonData = await resp.json();

      setBuylist(jsonData);

      console.log("Response", resp);
      console.log("List: ", buy_id);
    } catch (err) {
      console.error(err.message);
    }
  };
  useEffect(() => {
    console.log("Enter useEffect()");
    loadList();
  }, [buy_id]);
  // useEffect(() => {
  //   console.log("Enter useEffect");
  //   loadID();
  // }, []);
  return (
    <div className="container">
      <h2>Buy Detail</h2>
      <tr className="btn mt-5">
        <td>
          <Form.Control
          class="form-control mr-sm-2"
          type="search"
          placeholder="Search"
          onChange={(e) =>{
            setID(e.target.value)
          }}
          />
          {buy_list.map((item) => {
            return <p>{item.buy_id}</p>
          })}
        </td>
        <td>
          <a href="/insertbuydetail">
            <button className="btn btn-success ml-5 ">เพิ่มข้อมูล</button>
          </a>
        </td>
      </tr>
      <table className="table table-striped mt-5">
        <thead>
          <tr>
            <th>Buy Id</th>
            <th>Item</th>
            <th>Full Product Id</th>
            <th>Product Name</th>
            <th>Color</th>
            <th>Size</th>
            <th>Buy Amount</th>
            <th>Buy Cost</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {list.map((elt) => {
            return (
              <tr>
                <td>{elt.buy_id}</td>
                <td>
                  <a>{elt.item}</a>
                </td>
                <td>{elt.full_prod_id}</td>
                <td>{elt.prod_name}</td>
                <td>{elt.color}</td>
                <td>{elt.size}</td>
                <td>{elt.buy_amount}</td>
                <td>{elt.buy_cost}</td>
                <td>
                  <EditBuyDetail
                    buy_id={elt.buy_id}
                    item={elt.item}
                    full_prod_id={elt.full_prod_id}
                    prod_name={elt.prod_name}
                    color={elt.color}
                    size={elt.size}
                    buy_amount={elt.buy_amount}
                    buy_cost={elt.buy_cost}
                  />
                </td>
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => deleteItem(elt.full_prod_id)}
                  >
                    Del
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
