import React, { useEffect, useState } from "react";
import Form from "react-bootstrap/Form";

export default function SelectName(props) {
  const [full_prod_id, setFull] = useState([props.full_prod_id]);
  const [prod_name, setName] = useState([props.prod_name]);
  // const color ="Brown"
  
  const [prod_list, setList] = useState([]);

  const loadList = async () => {
    try {
      const bodyData = {full_prod_id, prod_name}
      const resp = await fetch(`http://localhost:5000/getBuydetail`,{
        method:"POST",
        headers:{"Content-Type": "application/json"},
        body: JSON.stringify(bodyData)
      }
      
      );
      const jsonData = await resp.json();

      setName(jsonData);

    //   console.log("Response", resp);
    //   console.log("List: ", color_list);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    console.log("Enter useEffect");
    loadList();
  }, []);

  return (
    <>
      
      <Form.Select aria-label="Default select"  className="form-control">
        <option value="prod_name">Select Product Name</option>
      {prod_list.map((full)=>{
        return (
          <>
            
            <option value={full_prod_id.prod_name} placeholder="name">{full_prod_id.prod_name}</option>
            
            
         </>
        )
      })}
      </Form.Select>
    </>
  );
}
