import React from 'react'

export default function Sale() {
    return (
        <div className="container">
        <h3 >SALE REPORT</h3>
        {/* {cost.map((elt)=>{return (<p>Total Cost:{elt.total} BAHT</p>)})} */}
        <div>
      <tr className="btn mt-1">
      <td>
          <Form.Control
          class="form-control mr-sm-2"
          type="search"
          placeholder="Search"
          onChange={(e) =>{
            setID(e.target.value)
          }}
          />
          {buy_list.map((item) => {
            return <p>{item.buy_id}</p>
          })}
        </td>
        <td>
          <a href="/insertbuy">
            <button className="btn btn-success ml-3">เพิ่มข้อมูล</button>
          </a>
        </td>
      </tr>
      </div>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Buy date</th>
            <th>Buy id</th>
            <th>Buy status</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {list.map((elt) => {
            return (
              <tr>
                <td>{elt.buy_date}</td>
                <td>
                  <a href="/buydetail">{elt.buy_id}</a>
                </td>
                <td>{elt.buy_status}</td>
                <td>
                  <EditBuy
                    buy_date={elt.buy_date}
                    buy_id={elt.buy_id}
                    buy_status={elt.buy_status}
                  />
                </td>
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => deleteItem(elt.buy_id)}
                  >Del</button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
    )
}
