import React, { useState, useEffect } from "react";

export default function Buy() {
  const [list, setList] = useState([]);
  const onUpdateData = (e)=>{
      e.preventDefault();
  }

  const deleteItem = async (buy_id) => {
    try {
      const del = await fetch(`http://localhost:5000/deleteBuy/${buy_id}`, {
        method: "DELETE",
      });
      setList(
        list.filter((elt) => {
          return elt.buy_id !== buy_id;
        })
      );
    } catch (err) {
      console.error(err.message);
    }
  };
  const loadList = async () => {
    try {
      const resp = await fetch("http://localhost:5000/getBuy");
      const jsonData = await resp.json();

      setList(jsonData);

      console.log("Resp", resp);
      console.log("List:", list);
    } catch (err) {
      console.error(err.message);
    }
  };
  useEffect(() => {
    console.log("Enter useEffect()");
    loadList();
  }, []);
  return (
    <div className="container">
      <h3>BUY REPORT</h3>
      <tr className="btn mt-5">
        <td>
          <input></input>
          <button className="btn btn-secondary ">ค้นหา</button>
        </td>
        <td>
          <button className="btn btn-success  ml-5 ">เพิ่มข้อมูล</button>
        </td>
      </tr>
      <table className="table table-striped mt-5">
        <thead>
          <tr>
            <th>Buy date</th>
            <th>Buy id</th>
            <th>Buy status</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {list.map((elt) => {
            return (
              <tr>
                <td>{elt.buy_date.slice(0, 10)}</td>
                <td>
                  <a href="/buydetail">{elt.buy_id}</a>
                </td>
                <td>{elt.buy_status}</td>
                <td>
                  <div>
                    <button
                      type="button"
                      className="btn btn-warning"
                      data-toggle="modal"
                      data-target="#myModal"
                    >
                      แก้ไข
                    </button>
                    {/* The Modal */}
                    <div className="modal" id="myModal">
                      <div className="modal-dialog">
                        <div className="modal-content">
                          {/* Modal Header */}
                          <div className="modal-header">
                            <h4 className="modal-title">Edit Buy Report</h4>
                            <button
                              type="button"
                              className="close"
                              data-dismiss="modal"
                            >
                              ×
                            </button>
                          </div>
                          {/* Modal body */}
                          <div className="modal-body">
                            <div>
                              <div className="form-group">
                                <input
                                  type="date"
                                  className="form-control"
                                  id={elt.buy_date}
                                />
                              </div>
                              <div className="form-group">
                                <input
                                  type="text"
                                  className="form-control"
                                  id={elt.buy_id}
                                />
                              </div>
                              <div className="form-group">
                                <input
                                  type="text"
                                  className="form-control"
                                  id={elt.buy_status}
                                />
                              </div>
                            </div>
                          </div>
                          {/* Modal footer */}
                          <div className="modal-footer">
                          <button
                              type="button"
                              className="btn btn-success"
                              data-dismiss="modal"
                              onClick={e=>{onUpdateData(e)}}
                            >
                              Save
                            </button>
                            <button
                              type="button"
                              className="btn btn-danger"
                              data-dismiss="modal"
                            >
                              Close
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </td>
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => deleteItem(elt.buy_id)}
                  >
                    ลบ
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
