import { configureStore } from '@reduxjs/toolkit'
import userReducer from './userSlice'
import {
    persistStore,
    persistReducer,
    FLUSH,
    REHYDRATE,
    PAUSE,
    PERSIST,
    PURGE,
    REGISTER,
  } from 'redux-persist'
  import storage from 'redux-persist/lib/storage'

  

export default configureStore({
    reducer:{
        user: userReducer,

    },
})
