import "./App.css";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Banner from "./components/Banner";
import Footer from "./components/Footer";
import Header from "./components/Header";
import Home from "./components/Home";
import Buy from "./components/Buy";
import Sale from "./components/Sale";
import Size from "./components/Size";
import Product from "./components/Product";
import Customer from "./components/Customer";
import InsertCustomer from "./components/InsertCustomer";
import Register from "./components/Register";
import Cart from "./components/Cart";
import Wishlist from "./components/Wishlist";
import Buydetail from "./components/Buydetail";
import ViewProduct from "./components/ViewProduct";
import InsertBuy from "./components/InsertBuy";
import InsertBuyDetail from "./components/InsertBuyDetail";
import LogIn from "./components/LogIn";

function App() {
  return (
    <div className="App">
      <Router>
        
        <Switch>
          <>
          <Header />
          
          <Route exact path="/">
            <Home />
          </Route>
          <Route exact path="/customer">
            <Customer />
          </Route>
          <Route exact path="/customer/:id">
            <Customer />
          </Route>
          <Route exact path="/insertCustomer">
            <InsertCustomer />
          </Route>
          <Route exact path="/product">
            <Product />
          </Route>
          {/* <Route exact path="/viewproduct">
            <ViewProduct/>
          </Route> */}
          <Route exact path="/viewproduct/:prod_color_id">
            <ViewProduct />
          </Route>
          <Route exact path="/buy">
            <Buy />
          </Route>
          <Route exact path="/insertbuy">
            <InsertBuy/>
          </Route>
          <Route exact path="/buy/:id">
            <Buy />
            </Route>
          <Route exact path="/insertbuydetail">
            <InsertBuyDetail/>
          </Route>
          <Route exact path="/buydetail">
            <Buydetail />
          </Route>
          {/* <Route exact path="/buydetail/:id">
            <Buydetail/>
          </Route> */}
          <Route exact path="/sale">
            <Sale />
          </Route>
          <Route exact path="/size">
            <Size />
          </Route>
          <Route exact path="/register">
            <Register />
          </Route>
          <Route exact path="/login">
            <LogIn/>
          </Route>
          <Route exact path="/cart">
            <Cart />
          </Route>
          <Route exact path="/wishlist">
            <Wishlist />
          </Route>

          <Footer />
          </>
        </Switch>

        
      </Router>
    </div>
  );
}

export default App;
