import React, { useState, useEffect } from "react";
import EditCustomer from "./EditCustomer";

export default function Customer() {
  const [list, setList] = useState([]);
  const deleteItem = async (id) => {
    try {
      // console.log("ID:",id)
      const del = await fetch(`http://localhost:5000/deleteCustom/${id}`, {
        method: "DELETE",
      });
      setList(
        list.filter((elt) => {
          return elt.id !== id;
        })
      );
    } catch (err) {
      console.error(err.message);
    }
  };

  const loadList = async () => {
    try {
      const resp = await fetch("http://localhost:5000/getCustomer");
      const jsonData = await resp.json();

      setList(jsonData);

      console.log("Resp", resp);
      console.log("List:", list);
    } catch (err) {
      console.error(err.message);
    }
  };
  useEffect(() => {
    console.log("Enter useEffect()");
    loadList();
  }, []);

  return (
    <div className="container">
      <h3>CUSTOMER</h3>
      <tr className="btn mt-5">
        {/* <td>
          <input></input>
          <button className="btn btn-secondary ">ค้นหา</button>
        </td> */}

        <div className="d-flex">
          <button
            type="search"
            placeholder="Search"
            className="me-2"
            aria-label="Search"
          />
          <button variant="outline-success">Search</button>
        </div>

        <td>
          <a href="/insertCustomer">
            <button className="btn btn-success ml-5 ">เพิ่มข้อมูล</button>
          </a>
        </td>
      </tr>
      <table className="table table-hover mt-5">
        <thead>
          <tr>
            <th>Cust Id</th>
            <th>Cust Name</th>
            <th>Cust Lname</th>
            <th>Phone Num</th>
            <th>Cust Card</th>
            <th>Edit</th>
            <th>Del</th>
          </tr>
        </thead>
        <tbody>
          {list.map((elt) => {
            return (
              <tr>
                <td>{elt.cust_id}</td>
                <td>{elt.cust_name}</td>
                <td>{elt.cust_lname}</td>
                <td>{elt.phone_num}</td>
                <td>{elt.credit_card}</td>
                <td>
                  <EditCustomer
                    cust_id={elt.cust_id}
                    cust_name={elt.cust_name}
                    cust_lname={elt.cust_lname}
                    phone_num={elt.phone_num}
                    credit_card={elt.credit_card}
                  />
                </td>
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => deleteItem(elt.id)}
                  >
                    Del
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
