import React, { useState } from "react";

export default function Edit(props) {
  const cust_id = props.cust_id;
  const [cust_name, setCust_Name] = useState(props.cust_name);
  const [cust_lname, setPrice] = useState(props.price);

  const onUpdateData = async (e) => {
    e.preventDefault();
    try {
      const bodyData = { name, price };
      const res = await fetch(`http://localhost:4000/update/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(bodyData),
      });
      window.location = "/";
      console.log(res);
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <div>
      <div>
        {/* Button to Open the Modal */}
        <button
          type="button"
          className="btn btn-primary"
          data-toggle="modal"
          data-target={`#id${id}`} //"#myModal"
        >
          Edit
        </button>
        {/* The Modal */}
        <div className="modal" id={`id${id}`}>
          <div className="modal-dialog">
            <div className="modal-content">
              {/* Modal Header */}
              <div className="modal-header">
                <h4 className="modal-title">Edit Fruits</h4>
                <button type="button" className="close" data-dismiss="modal">
                  ×
                </button>
              </div>
              {/* Modal body */}
              <div className="modal-body">
                <div>
                  <div class="form-group">
                    <input
                      type="text"
                      className="form-control"
                      id="usr"
                      value={cust_name}
                      onChange={(e) => {
                        setCust_Name(e.target.value);
                      }}
                    />
                  </div>
                  <div>
                  <div class="form-group">
                    <input
                      type="text"
                      className="form-control"
                      id="usr"
                      value={cust_lname}
                      onChange={(e) => {
                        setCust_Lname(e.target.value);
                      }}
                    />
                  </div>
                  <div className="form-group">
                    <input
                      type="number"
                      className="form-control"
                      id="pwd"
                      value={phone_num}
                      onChange={(e) => {
                        setPhone_Num(e.target.value);
                      }}
                    />
                  </div>
                  <div className="form-group">
                    <input
                      type="number"
                      className="form-control"
                      id="pwd"
                      value={credit_card}
                      onChange={(e) => {
                        setCredit_Card(e.target.value);
                      }}
                    />
                  </div>
                </div>
              </div>
              {/* Modal footer */}
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-warning"
                  data-dismiss="modal"
                  onClick={(e) => {
                    onUpdateData(e);
                  }}
                >
                  Save
                </button>
                <button
                  type="button"
                  className="btn btn-danger"
                  data-dismiss="modal"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    </div>
  );
}
